mapper.select:
select * from config_experience_fee
left join  user
on current_id = user.id
where current_id = #{currentId}

   and  remark like CONCAT('%', #{remark}, '%')


   and  is_configure_experience_fee = #{isConfigureExperienceFee}

select * from config_experience_fee left join user on current_id = user.id where 1=1
and remark like CONCAT('%', #{remark}, '%')
and is_configure_experience_fee = #{isConfigureExperienceFee}
order by is_configure_experience_fee asc ,config_experience_fee.id desc limit #{page},#{size}

select count(1) from config_experience_fee left join user on current_id = user.id where 1=1
and remark like CONCAT('%', #{remark}, '%')
and is_configure_experience_fee = #{isConfigureExperienceFee}
