mapper.select:
select
*
from user
where superior_id = #{userId}
order by id desc
limit #{page},#{size}

select id from user

where root_id in

          #{id}

select count(1) from user where has_flow = 1

and root_id in

  #{id}

select count(1) from user where  has_flow = 1 and create_time>=date(now())

  and  root_id in

    #{id}

select * from `user` where has_flow = 1 and create_time>=#{localDate} and create_time <#{plusDays}

      and  root_id in

        #{id}
